// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract CertificateRegistry {
    struct Certificate {
        uint256 csn; // Certificate Serial Number
        string cid; // IPFS Hash (CID)
        string emailId;
    }

    mapping(uint256 => Certificate) public certificates;

    event CertificateAdded(uint256 csn, string cid, string emailId);

    function addCertificate(uint256 _csn, string memory _cid, string memory _emailId) public {
        // Check if the certificate with the given CSN already exists
        require(certificates[_csn].csn == 0, "Certificate with this CSN already exists");

        // Create a new certificate
        Certificate memory newCertificate = Certificate(_csn, _cid, _emailId);

        // Store the certificate in the mapping
        certificates[_csn] = newCertificate;

        // Emit an event
        emit CertificateAdded(_csn, _cid, _emailId);
    }

    function getCertificate(uint256 _csn) public view returns (uint256, string memory, string memory) {
        Certificate storage cert = certificates[_csn];
        require(cert.csn != 0, "Certificate with this CSN does not exist");
        return (cert.csn, cert.cid, cert.emailId);
    }
}
