pragma solidity >=0.4.22 <0.9.0;

pragma experimental ABIEncoderV2; // Enable experimental ABI encoder v2

contract CertificateRegistryV2 {
    struct Certificate {
        uint256 csn; // Certificate Serial Number
        string cid; // IPFS Hash (CID)
        string emailId;
    }

    mapping(uint256 => Certificate) public certificates;
    mapping(uint256 => bool) public csnExists; // Mapping to check if a given CSN exists

    event CertificateAdded(uint256 csn, string cid, string emailId);

    // Function to add multiple certificates at once
    function addCertificates(Certificate[] memory _certificates) public {
        for (uint256 i = 0; i < _certificates.length; i++) {
            uint256 _csn = _certificates[i].csn;
            require(!csnExists[_csn], "Certificate with this CSN already exists");

            // Mark the CSN as existing
            csnExists[_csn] = true;

            // Store the certificate in the mapping
            certificates[_csn] = _certificates[i];

            // Emit an event
            emit CertificateAdded(_csn, _certificates[i].cid, _certificates[i].emailId);
        }
    }

    // Struct to hold buffer object
    struct Buffer {
        uint256 csn;
        string cid;
        string emailId;
    }

    // Function to return a list of buffer objects for the input parameter list of CSNs
    function getBufferForCSNs(uint256[] memory _csns) public view returns (Buffer[] memory) {
        Buffer[] memory buffers = new Buffer[](_csns.length);

        for (uint256 i = 0; i < _csns.length; i++) {
            Certificate storage cert = certificates[_csns[i]];
            require(cert.csn != 0, "Certificate with this CSN does not exist");

            // Create buffer object
            Buffer memory buffer = Buffer(cert.csn, cert.cid, cert.emailId);
            buffers[i] = buffer;
        }

        return buffers;
    }
}
